sys_variable_value.value:
(function execute(inputs, outputs) {
      var responseObject = JSON.parse(inputs.response_body);

  		var games = [];

  		responseObject.results.forEach(function(game){
          var currentObject = {};
              currentObject.gameName = game.name,
              currentObject.gameRating = game.rating, 
              currentObject.gamePlaytime = game.playtime, 
              currentObject.releaseDate = game.released
			games.push(currentObject);
        })

  	  outputs.results = games;

  })(inputs, outputs);

